import React, {Component} from 'react';
import axios from "axios";
class Search extends Component {
    search_github=()=>{
        this.props.Up_data_app_state({isfirst: false,is_login: true})
        axios.get(`https://api.github.com/search/users?q=${this.search.value}`).then(
            response=>{//成功调用github
                this.props.Up_data_app_state({users:response.data.item,is_login: false})
                },
            error=>{axios.get('http://127.0.0.1:8000/search/users2').then(
                response=>{//调用本地服务器
                    this.props.Up_data_app_state({users:response.data.item,is_login: false})
                },
                error=>{this.props.Up_data_app_state({err:error,is_login: false})}
            )}
        )
    }
    render() {
        return (
            <div>
                <section className="jumbotron">
                    <h3 className="jumbotron-heading">Search Github Users</h3>
                    <div>
                        <input ref={(c)=>{this.search=c}} type="text" placeholder="enter the name you search"/>&nbsp;
                        <button onClick={this.search_github}>Search</button>
                    </div>
                </section>
            </div>
        );
    }
}

export default Search;
