import React, {Component} from 'react';
import {nanoid} from "nanoid";

class Person extends Component {
    state ={
        person:[
            { id:'001',name:'第一个人',age:18},
            { id:'002',name:'第二个人',age:19},
            { id:'003',name:'第三个人',age:20},
        ]
    }
    addPerson =()=>{
        const {person}=this.state
        const name=this.nameNode.value
        const age=this.ageNode.value
        console.log(name,age,nanoid())
        const NewPerson={id:nanoid(),name:name,age:age}
        this.setState({person:[NewPerson,...person]})
    }
    render() {
        return (
            <div>
                <h2>我是person组件</h2>
                <input ref={c => this.nameNode=c} type="text" placeholder="请输入姓名"/>
                <input ref={c=>this.ageNode =c} type="text" placeholder="请输入年龄"/>
                &nbsp;
                <button onClick={this.addPerson}>添加</button>
                <ul>
                    {
                        this.state.person.map((personObj)=>{
                            return <li key={personObj.id}>名字：{personObj.name} 年龄： {personObj.age}</li>
                        })
                    }
                </ul>
            </div>
        );
    }
}


export default Person;
