import React from "react";
import Footer from "./Footer/Footer";
import Header from "./Header/Header";
import List from "./List/List";
import './App.css';

class App extends React.Component{
    state={
        todos:[
            {id:'001',name:"吃饭",done:true},
            {id:'002',name:"睡觉",done:true},
            {id:'003',name:"打代码",done:false},
            {id:'004',name:"唱歌",done:true},
            {id:'005',name:"洗澡",done:false},
            {id:'006',name:"玩游戏",done:true}
        ]
    }
    handle_todos = (todoObj)=>{
        const todos=this.state.todos
        console.log(todos)
        const new_todos=[todoObj,...todos]
        this.setState({todos:new_todos})
        console.log(new_todos)
    }
    UP_data_todos=(id,done)=>{
        const {todos}=this.state;
        const new_todos=todos.map((todoObj)=>{
            if (id===todoObj.id) return {...todoObj,done:done}
            else return {...todoObj}
        })
        this.setState({todos: new_todos})
    }
    delete_todo=(id)=>{
        const {todos}=this.state
       const new_todos= todos.filter((todoObj)=>{
            return id!==todoObj.id
        })
        this.setState({todos: new_todos})
        console.log(id)
    }
    render() {
        const {todos}=this.state
        return (
            <div className="todo-container">
                <div className="todo-wrap">
                    <Header handle_todos={this.handle_todos}/>
                    <List  todos={todos} Up_data_todos={this.UP_data_todos} delete_todo={this.delete_todo}/>
                    <Footer todos={todos}/>
                </div>
            </div>
        );
    }
}

export default App;
