import React, {Component} from 'react';
import store from '../../redux/store'
import stroe from "../../redux/store";
import {incrementAction,decrementAction} from "../../redux/count_action";
class Count extends Component {

    increment =()=>{
            const {value}=this.selectNumber
        stroe.dispatch(incrementAction(value))

    }
    decrement =()=>{
        const {value}=this.selectNumber
        stroe.dispatch(decrementAction(value))

    }
    incrementOdd =()=>{
        const {value}=this.selectNumber
        const count=store.getState()
        if (count%2!==0){
            stroe.dispatch(incrementAction(value))
        }
    }
    incrementAnscy =()=>{
        const {value}=this.selectNumber
        setTimeout(()=>{
            stroe.dispatch(incrementAction(value))
        },5000)
    }
    render() {
        return (
            <div className="App">
                <h1>当前求和为{store.getState()}</h1>
                <select ref={(c)=>{this.selectNumber=c}}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                </select>&nbsp;&nbsp;
                <button onClick={this.increment}>+</button>&nbsp;
                <button onClick={this.decrement}>-</button>&nbsp;
                <button onClick={this.incrementOdd}>当前为奇数加</button>&nbsp;
                <button onClick={this.incrementAnscy}>异步加</button>&nbsp;
            </div>
        );
    }
}

export default Count;
