import React, {Component} from 'react';
import './Footer.css'
class Footer extends Component {
    done_all=(event)=>{
        this.props.done_all_todos(event.target.checked);
    }
    clear_all_done=()=>{
        console.log('点击按钮清除所有已完成')
        this.props.clear_all();
    }


    render() {
        const {todos}=this.props
        const total=todos.length
        const done_true=todos.reduce((pre,todoObj)=>{return todoObj.done? pre+1:pre+0},0)
        return (
            <div className="todo-footer">
                <label>
                    <input type="checkbox" onChange={this.done_all} checked={total===done_true&& total!==0 ? true:false}/>
                </label>
                <span>
          <span>已完成{done_true}</span> / 全部{total}
        </span>
                <button onClick={this.clear_all_done} className="btn btn-danger">清除已完成任务</button>
            </div>
        );
    }
}

export default Footer;
