    const date= moment()
    return(
        <div>
            <countContext.Provider value={count}>
                <Example1/>
            </countContext.Provider>
            <button onClick={()=>{
                setCount(count+1)
            }} >点我加1</button>
            <p>
                测试时间显示
            </p>
        </div>
    )
}
export default Context
