import React, {Component} from 'react';
import './Todo.css'

class Todo extends Component {
    state ={mouse:false}
    handle_checked=(id)=>{
        return (event)=>{
            this.props.Up_data_todos(id,event.target.checked)
            console.log(id,event.target.checked)
        }
    }

    mouse_over=(flag)=>{
        return ()=>{
            this.setState({mouse:flag})
        }
    }

    handle_delete= (id)=>{
        console.log(id)
        this.props.delete_todo(id)
    }

    render() {
        const {id,name,done}=this.props
        //判断鼠标是否进去标签中
        const {mouse}=this.state
        return (
            <li style={{backgroundColor: mouse? '#ddd':'white'}} onMouseEnter={this.mouse_over(true)} onMouseLeave={this.mouse_over(false)}>
                <label>
                    <input type="checkbox" defaultChecked={done} onChange={this.handle_checked(id)}/>
                    <span>{name}</span>
                </label>
                <button onClick={()=>{this.handle_delete(id)}} className="btn btn-danger" style={{display:mouse ? 'block':'none'}}>删除</button>
            </li>
        );
    }
};


export default Todo;
