import React, {Component} from 'react';
import './Todo.css'

class Todo extends Component {
    state ={mouse:true}
    mouse_over=(flag)=>{
        return ()=>{
            console.log(flag)
        }
    }
    render() {
        const {name,done}=this.props
        return (
            <li style={{backgroundColor: this.state.mouse? '#ddd':'white'}} onMouseEnter={this.mouse_over(true)} onMouseLeave={this.mouse_over(false)}>
                <label>
                    <input type="checkbox" defaultChecked={done}/>
                    <span>{name}</span>
                </label>
                <button className="btn btn-danger" style={{display:'none'}}>删除</button>
            </li>
        );
    }
}


export default Todo;
