import React, {Component} from 'react';
import Search from "./compontens/Search";
import List from "./compontens/List";
import './App.css'
class App extends Component {
    state={users:[],
    isfirst:true,
    is_login:false,
    err:''}//初始化数组users
    save_users=(users)=>{
        console.log('成功传入数据')
        this.setState({users:users})
        console.log(users)
    }
    // display_users=()=>{
    //     const {users}=this.state
    //
    // }
    render() {
        return (
            <div className="container">
                <Search save_users={this.save_users} />
                <List users={this.state.users}/>
            </div>
        );
    }
}

export default App;
