import React, {Component} from 'react';
import axios from "axios";
import PubSub from 'pubsub-js'
class Search extends Component {
    search_github=()=>{
        PubSub.publish('state_github',{name:'jeert',age:18})
        // this.props.Up_data_app_state({isfirst: false,is_login: true})
        // axios.get(`https://api.github.com/search/users?q=${this.search.value}`).then(
