        //         },
        //         error=>{this.props.Up_data_app_state({err:error,is_login: false})}
        //     )}
        // )
    }
    render() {
        return (
            <div>
                <section className="jumbotron">
                    <h3 className="jumbotron-heading">Search Github Users</h3>
                    <div>
                        <input ref={(c)=>{this.search=c}} type="text" placeholder="enter the name you search"/>&nbsp;
                        <button onClick={this.search_github}>Search</button>
                    </div>
                </section>
            </div>
        );
    }
}

export default Search;
