import React, {Component} from 'react';
import './Todo.css'

class Todo extends Component {
    state ={mouse:false}
    mouse_over=(flag)=>{
        return ()=>{
            this.setState({mouse:flag})
        }
    }
    render() {
        const {name,done}=this.props
        //判断鼠标是否进去标签中
        const {mouse}=this.state
        return (
            <li style={{backgroundColor: mouse? '#ddd':'white'}} onMouseEnter={this.mouse_over(true)} onMouseLeave={this.mouse_over(false)}>
                <label>
                    <input type="checkbox" defaultChecked={done}/>
                    <span>{name}</span>
                </label>
                <button className="btn btn-danger" style={{display:mouse ? 'none':'none'}}>删除</button>
            </li>
        );
    }
}


export default Todo;
