import React, {useEffect, useState} from "react";
function Test() {
    const [value,setValue]=useState('')
    const [count,setCount]=useState(0)
    useEffect(()=>{
        if(count%2==0){
            console.log('改变为偶数')
        }
        else{
            console.log('改变为奇数')
        }
        return
    },[count])
        return (
            <div>
                <input onChange={(e)=>{setValue(e.target.value)}} value={value} type="text"/>
                <button onClick={()=>{console.log(value)}}> 点击加载数值 </button>
                <button onClick={()=>{
                    setCount(count+1)
                }}> 点击更新数值 </button>
            </div>
        )
}
export default Test
