import React, {Component} from 'react';
import Detail from "./Detail/Detail";
import {Route} from "react-router-dom";
import MyNavLink from "../../MyNavLink";

class News extends Component {
    state={
        news_array:[
            {id:"01",title:"这是标题1"},
            {id:"02",title:"这是标题2"},
            {id:"03",title:"这是标题3"},
            {id:"04",title:"这是标题4"}
        ]
    }
    render() {
        return (
            <div>
            <ul>
                {
                    this.state.news_array.map((NewsObj)=>{
                        return <li  key={NewsObj.id}>
                            <MyNavLink className="News_content" to={{pathname:`/home/news/detail/${NewsObj.id}`,state:{id:NewsObj.id,title:NewsObj.title}}}>
                                id:{NewsObj.id},{NewsObj.title}
                            </MyNavLink>
                            {/*注册路由*/}
                        </li>
                    })
                }
            </ul>
                <hr/>
                <Route path="/home/news/detail" component={Detail}/>
            </div>
        );
    }
}

export default News;
